Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."AUDIT_ASSIGNMENT_RESOURCE_FCT" 
      (resourceid NUMBER)
      RETURN VARCHAR2
          IS
          uname VARCHAR2(120);
          BEGIN
          SELECT sr.unique_name into uname FROM srm_resources sr WHERE sr.id = resourceid;			 
          RETURN uname;
          END AUDIT_ASSIGNMENT_RESOURCE_FCT;
