Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."AUDIT_SRM_RESOURCE_NAME_FCT" 
      (resourceid NUMBER)
      RETURN VARCHAR2
          IS
          resname VARCHAR2(300);
          BEGIN
          SELECT res.full_name into resname FROM srm_resources res WHERE res.id = resourceid;
          RETURN resname;
          END AUDIT_SRM_RESOURCE_NAME_FCT;
