Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."AUDIT_TIMESHEET_NAME_FCT" 
    (timeperiodid NUMBER, resourceid NUMBER)
    RETURN VARCHAR2
      IS
      timeperiod VARCHAR2(60);
      resname VARCHAR2(300);
      BEGIN
        SELECT TO_CHAR(period.prstart, 'YYYY-MM-DD"T"HH24:MI:SS') || '#' || TO_CHAR(period.prfinish, 'YYYY-MM-DD"T"HH24:MI:SS')
        INTO timeperiod FROM prtimeperiod period WHERE period.prid = timeperiodid;
        SELECT res.full_name into resname FROM srm_resources res WHERE res.id = resourceid;
        RETURN resname || '#' || timeperiod;
      END AUDIT_TIMESHEET_NAME_FCT;
