Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_AUTONUM_NEXT_VALUE_FCT" (
   P_TYPE  IN VARCHAR2,
   P_VALUE IN VARCHAR2,
   P_COUNTER_LENGTH IN INTEGER
   )
   RETURN VARCHAR2
IS
   V_RETURN_VALUE VARCHAR2(100);
BEGIN

   IF P_TYPE = 'TEXT' THEN
     RETURN (P_VALUE);
   END IF;

   IF P_TYPE = 'NUMERIC' THEN
     V_RETURN_VALUE := LPAD(P_VALUE, P_COUNTER_LENGTH, '0');
   END IF;

   IF P_TYPE = 'ALPHANUMERIC' THEN
     V_RETURN_VALUE := LPAD(CMN_AUTONUM_TO_ALPHANUM_FCT(P_VALUE), P_COUNTER_LENGTH, '0');
   END IF;

   RETURN V_RETURN_VALUE;

END;
