Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_AUTONUM_TO_ALPHANUM_FCT" (
   P_VALUE IN NUMBER
   )
   RETURN VARCHAR2
IS
   V_VALUE     NUMBER       := P_VALUE;
   V_REMAINDER NUMBER       := 0;
   V_ALPHANUM  VARCHAR2(40) := '';
BEGIN

   /* Convert a number to an alphanumeric string using a radix of 36.
      The following ASCII characters are used as digits:
      0123456789abcdefghijklmnopqrstuvwxyz */

   WHILE V_VALUE >= 36
   LOOP
      V_REMAINDER := MOD(V_VALUE, 36);
      V_VALUE     := TRUNC(V_VALUE / 36);
      V_ALPHANUM  := CMN_AUTONUM_TO_CHAR_FCT(V_REMAINDER) || V_ALPHANUM;
   END LOOP;
   V_ALPHANUM := CMN_AUTONUM_TO_CHAR_FCT(V_VALUE) || V_ALPHANUM;

   RETURN V_ALPHANUM;
END;
