Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_AUTONUM_TO_NUM_FCT" (
   P_VALUE IN VARCHAR2
   )
   RETURN NUMBER
IS
   V_CH       VARCHAR2(1);
   V_LENGTH   NUMBER;
   V_RESULT   NUMBER := 0;
   V_POSITION NUMBER := 1;
BEGIN

   /* Convert an alphanumeric string to a numeric using a radix of 36.
      The following ASCII characters are used as digits:
      0123456789abcdefghijklmnopqrstuvwxyz */

   V_LENGTH := LENGTH(P_VALUE);
   IF V_LENGTH > 0
   THEN
      WHILE V_POSITION <= V_LENGTH
      LOOP
         V_CH := SUBSTR(P_VALUE, V_POSITION, 1);
         V_RESULT := V_RESULT * 36;
         V_RESULT := V_RESULT + CMN_AUTONUM_TO_DIGIT_FCT( V_CH );
         V_POSITION := V_POSITION + 1;
      END LOOP;
   END IF;

   RETURN V_RESULT;
END;
