Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_DATEDIFF_FCT" (
   P_DATE1   IN   DATE,
   P_DATE2   IN   DATE
)
   RETURN INTEGER
IS
   V_DAYS_DIFFERENCE             INTEGER;

BEGIN
   V_DAYS_DIFFERENCE := 0;

   V_DAYS_DIFFERENCE := P_DATE1 - P_DATE2;

   IF V_DAYS_DIFFERENCE IS NULL
   THEN
      V_DAYS_DIFFERENCE := 0;
   END IF;

   RETURN V_DAYS_DIFFERENCE;
END;
