Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_DB_VERSION" 
return integer
is
  verStr varchar2(40);
  dotPos integer;
  result integer;
begin
  result := -1;
  select value into verStr
  from nls_database_parameters
  where parameter='NLS_RDBMS_VERSION';
  dotPos := instr(verStr, '.');
  if (dotPos > 0) then
    result := to_number(substr(verStr, 1, dotPos));
  end if;
  return result;
end;
