Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_GET_PLAIN_TEXT_FCT" ( P_RICH_TEXT CLOB )
RETURN CLOB
IS
 plain_text clob;
BEGIN
	SELECT json_value(P_RICH_TEXT,'$.plainText') INTO plain_text FROM dual;
	IF P_RICH_TEXT IS NOT NULL AND plain_text IS NULL AND INSTR(P_RICH_TEXT, '<plain-text>') > 0 THEN
        SELECT SUBSTR(P_RICH_TEXT,INSTR(P_RICH_TEXT, '<plain-text>')+LENGTH('<plain-text>'), INSTR(P_RICH_TEXT, '</plain-text>')-(INSTR(P_RICH_TEXT, '<plain-text>')+LENGTH('<plain-text>'))) INTO plain_text FROM dual;
	END IF;
	RETURN COALESCE(plain_text,P_RICH_TEXT);
END;
