Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_GET_SEQUENCE_FCT" (P_TABLE_NAME IN VARCHAR2)
   RETURN VARCHAR2
IS
   V_SEQUENCE_NAME               VARCHAR2 (100);
BEGIN
   SELECT   NVL (MAX (SEQUENCE_NAME), NULL)
   INTO     V_SEQUENCE_NAME
   FROM     USER_SEQUENCES
   WHERE    SEQUENCE_NAME = P_TABLE_NAME || '_S1';
   RETURN V_SEQUENCE_NAME;
END;
