Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_ID_NEXTVAL_FCT" (
   p_table_name      in   varchar2,
   p_sequence_name   in   varchar2 default null
)
   return integer
is
   v_curr_id                     integer;
begin
   v_curr_id := 0;

	 cmn_id_sp ( p_table_name, v_curr_id, 1, p_sequence_name );

   return v_curr_id;
end;
