CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_LOCKED_BY_FCT" ( P_TABLE_NAME VARCHAR2, P_LOCK_NAME VARCHAR2, P_RECORD_ID NUMBER ) RETURN NUMBER /* Description: ------------ This function returns the user id of the user that holds the lock. If there is no lock it will return 0.
Return Values: -------------- 0 if there is no lock or any other error occurred. <user_id> if there is a lock
*/ IS V_USER_ID NUMBER; BEGIN SELECT NVL (MAX (PRUSERID), -9) INTO V_USER_ID FROM PRLOCK WHERE PRTABLENAME = P_TABLE_NAME AND PRNAME = P_LOCK_NAME AND PRRECORDID = P_RECORD_ID;
IF V_USER_ID >= 0 THEN RETURN (V_USER_ID); ELSE RETURN (0); END IF; EXCEPTION WHEN OTHERS THEN RETURN (-1); END;
