Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMN_TIME_LABEL_FCT" ( P_LANGUAGE_CODE VARCHAR2, P_TIME_TYPE VARCHAR2, P_DAY_KEY VARCHAR2, P_WEEK INTEGER, P_WEEK_KEY VARCHAR2, P_MONTH INTEGER, P_QUARTER INTEGER, P_YEAR INTEGER ) RETURN VARCHAR2 IS V_LABEL VARCHAR2 (100) := NULL; BEGIN
-- Get Label
IF P_TIME_TYPE = 'DAY' THEN V_LABEL := P_DAY_KEY; ELSIF P_TIME_TYPE = 'YEAR' THEN V_LABEL := P_YEAR; ELSIF P_TIME_TYPE = 'MONTH' THEN SELECT NAME INTO V_LABEL FROM CMN_LOOKUPS_V WHERE LOOKUP_TYPE = 'CMN_TIME_TYPES' AND LOOKUP_CODE = TO_CHAR (P_MONTH) AND LANGUAGE_CODE = P_LANGUAGE_CODE; ELSE SELECT NAME INTO V_LABEL FROM CMN_LOOKUPS_V WHERE LOOKUP_TYPE = 'CMN_TIME_TYPES' AND LOOKUP_CODE = P_TIME_TYPE AND LANGUAGE_CODE = P_LANGUAGE_CODE; END IF;
-- Concatenate label according to language/locale
IF P_LANGUAGE_CODE IN ('de', 'en', 'fr', 'es', 'ja') THEN IF P_TIME_TYPE = 'WEEK' THEN V_LABEL := V_LABEL || ' ' || P_WEEK || ', ' || SUBSTR(P_WEEK_KEY, 1, 4); ELSIF P_TIME_TYPE = 'MONTH' THEN V_LABEL := V_LABEL || ' ' || P_YEAR; ELSIF P_TIME_TYPE = 'QUARTER' THEN V_LABEL := V_LABEL || ' ' || P_QUARTER || ', ' || P_YEAR; ELSE NULL; END IF; END IF;
RETURN (V_LABEL); END;
