Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CMS_GET_CONTENT_ITEM_DESC_FCT" (
  p_odf_pk     in   number,
  p_type       in   varchar2,
  p_lang_code  in   varchar2 default 'en'
 )
 RETURN varchar2
IS
  v_i18n_supported integer;
  v_table_name varchar2(30);
  v_description_column varchar2(100);
  v_dynamic_sql varchar2(500);
  v_description varchar2(500);
BEGIN
  SELECT i18n_supported, table_name, description_column
    INTO v_i18n_supported, v_table_name, v_description_column
    FROM cms_content_types
   WHERE type = p_type;

  IF v_i18n_supported = 1 THEN
    IF p_type = 'PROCESS' THEN
        SELECT nls.description INTO v_description
        FROM bpm_def_processes bdp, bpm_def_process_versions bdpv, cmn_captions_nls nls
        WHERE bdpv.id = p_odf_pk
        AND bdp.id = bdpv.process_id
        AND nls.table_name = v_table_name
        AND nls.language_code = p_lang_code
        AND nls.pk_id = bdp.id;
      ELSE
        SELECT description INTO v_description
        FROM cmn_captions_nls
        WHERE pk_id = p_odf_pk
        AND table_name = v_table_name
        AND language_code = p_lang_code;
      END IF;
  ELSE
    v_dynamic_sql := 'SELECT ' || v_description_column || ' FROM ' || v_table_name || ' WHERE id = ' || p_odf_pk;
    EXECUTE IMMEDIATE v_dynamic_sql INTO v_description;
  END IF;

  RETURN v_description;

EXCEPTION
  WHEN OTHERS
    THEN
      RETURN NULL;
END;
