Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."COP_CALC_FINISH_FCT" (
    p_date_in IN DATE
) 
    RETURN DATE
IS
    v_date_out DATE;
BEGIN
    --
    -- This function returns the Datetime in the format
    -- 2001-12-05 00:00:00.000 and also does the finish date calculation
    -- It drops the time portion.
    --
    IF p_date_in IS NULL THEN
        RETURN NULL;
    END IF;
    IF TO_CHAR(p_date_in,'HH:MI:SS AM') = '12:00:00 AM' THEN
        v_date_out := TRUNC(p_date_in - 1);
    ELSE
        v_date_out := TRUNC(p_date_in);
    END IF;
    RETURN v_date_out;
END;
