Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."COP_CAL_DATE_ADD_FCT" (
    p_date DATE,
    p_type VARCHAR2,
    p_count NUMBER
)
    RETURN DATE
IS
    v_new_date DATE;
BEGIN
    IF p_type = 'DAY' THEN
        v_new_date := p_date + p_count;
    ELSIF p_type = 'WEEK' THEN
        v_new_date := p_date + (p_count * 7);
    ELSIF p_type = 'MONTH' THEN
        v_new_date := ADD_MONTHS(p_date,p_count);
    ELSIF p_type = 'QUARTER' THEN
        v_new_date := ADD_MONTHS(p_date,(p_count*3));
    ELSIF p_type = 'YEAR' THEN
        v_new_date := ADD_MONTHS(p_date,(p_count*12));
    END IF;
    RETURN TRUNC(v_new_date);
END COP_CAL_DATE_ADD_FCT;
