Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."COP_DATE_TRUNC_FCT" (
  P_DATE   IN   DATE
)
   RETURN DATE
IS
   V_TRUNC_DATE       DATE;
BEGIN

   IF P_DATE IS NULL
   THEN
      RETURN NULL;
   ELSE
      V_TRUNC_DATE := TRUNC(P_DATE);
      RETURN V_TRUNC_DATE;
   END IF;

END COP_DATE_TRUNC_FCT;
