Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."COP_DAYS_LATE_FCT" (
    p_finish_date IN DATE,         /* If project, project finish.
                                      If task, task finish. */
    p_base_finish_date IN DATE,    /* If project, project baseline finish.
                                      If task, task baseline finish. */
    p_progress IN INTEGER          /* If project, project progress.
                                      If task, task status. */
) 
    RETURN INTEGER
IS
    cf_date DATE;
    bf_date DATE;
    c_date DATE;
    days INTEGER;
BEGIN
    /* ****************************************************************************************************************************
    * Schedule (stoplight) and Days Late (value):                                                                                 *
    * If there is a Baseline and Finish Date is not equal to Baseline Finish Date, then Finish Date – Baseline Finish Date        *
    * (This calculates days late positive or negative based on baseline).                                                         *
    *                                                                                                                             *
    * If there is a Baseline and Finish Date equals Baseline Finish Date and Project Progress (or Task Status) is not completed   *
    * and the Finish Date is before Today’s Date, then Today’s Date – Finish Date  (This calculates days late positive if the     *
    * project/task isn’t late according to the baseline, but it isn’t completed and it is has a finish date before today’s date). *
    *                                                                                                                             *
    * If there is no Baseline and Project Progress (or Task Status) is not completed and the Finish Date is before Today’s Date,  *
    * then Today’s Date – Finish Date (This calculates days late positive if there is not any baseline and the project/task isn’t *
    * completed and it is has a finish date before today’s date).                                                                 *
    ******************************************************************************************************************************/
    cf_date := cop_calc_finish_fct(p_finish_date);
    bf_date := cop_calc_finish_fct(p_base_finish_date);
    c_date := TRUNC(SYSDATE);

    IF bf_date IS NULL THEN
        IF p_progress <> 2 AND c_date > cf_date THEN
            days := c_date - cf_date;
        ELSE
            days := 0;
        END IF;
    ELSE
        IF bf_date = cf_date THEN
            IF p_progress <> 2 AND c_date > cf_date THEN
                days := c_date - cf_date;
            ELSE
                days := 0;
            END IF;
        ELSE
            days := cf_date - bf_date;
        END IF;
    END IF;
    RETURN days;

END COP_DAYS_LATE_FCT;
