Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_AVERAGE_SLICES_TABLE_FCT" ( p_cursor SYS_REFCURSOR, p_period_type VARCHAR, p_start DATE, p_finish DATE, p_periods NUMBER, p_cal_type VARCHAR DEFAULT 'DEFAULT', p_options VARCHAR DEFAULT NULL )
RETURN CRV_SLICES PIPELINED PARALLEL_ENABLE ( PARTITION p_cursor BY ANY )
IS
BEGIN
  FOR ao_rec IN (
    select * from table( crv_sql_curve_pkg.sum_slices_table( p_cursor, p_period_type, p_start, p_finish, p_periods, p_cal_type, p_options || ',AVERAGE' ) )
  )
  LOOP
    pipe row( CRV_SLICE( ao_rec.id, ao_rec.field, ao_rec.scenario, ao_rec.period, ao_rec.start_date, ao_rec.finish_date, ao_rec.slice, ao_rec.calendar_key, ao_rec.period_name, ao_rec.group_by ) );
  END LOOP;
  RETURN;
END;
