Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_CALENDAR_FCT" ( p_period_type VARCHAR, p_start DATE, p_finish DATE, p_periods NUMBER, p_cal_type VARCHAR DEFAULT 'DEFAULT', p_options VARCHAR DEFAULT NULL )
   RETURN CRV_CALENDAR
IS
 v_calendar CRV_CALENDAR;

BEGIN
  v_calendar := crv_sql_curve_pkg.calendar( p_period_type, p_start, p_finish, p_periods, p_cal_type, p_options );
  return v_calendar;

END;
