Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_DEFAULT_CALENDAR_FCT" ( p_period_type VARCHAR, p_start DATE, p_finish DATE, p_periods NUMBER, p_options VARCHAR DEFAULT NULL  )
   RETURN CRV_CALENDAR
IS
 v_calendar CRV_CALENDAR;
 v_start DATE;
 v_finish DATE;
 idx NUMBER;
BEGIN
  v_calendar := crv_sql_curve_pkg.calendar( p_period_type, p_start, p_finish, p_periods, 'DEFAULT', p_options );
  return v_calendar;
END;
