Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_SEGMENTS_FCT" ( ID NUMBER, p_field_id NUMBER, p_scenario_id NUMBER, p_curve CLOB, p_start INTEGER DEFAULT 2, p_finish INTEGER DEFAULT 65534, p_options VARCHAR DEFAULT NULL )
  RETURN CRV_SEGMENTS PIPELINED
/*
*/
IS
BEGIN
  FOR ao_rec IN (
    with curves as (
      select id, p_field_id, p_scenario_id, p_curve from dual
    )
    select * from table( crv_sql_curve_pkg.segments_table( cursor( select * from curves ), p_start, p_finish, p_options ) )
  )
  LOOP
    pipe row( CRV_SEGMENT( ao_rec.id, ao_rec.field, ao_rec.scenario, ao_rec.segment_start, ao_rec.segment_finish, ao_rec.rate ) );
  END LOOP;
  RETURN;
END;
