Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_SEGMENTS_ROW_FCT" ( ID NUMBER, p_field_id NUMBER, p_scenario_id NUMBER, p_start INTEGER DEFAULT 0, p_finish INTEGER DEFAULT 99999999, p_options VARCHAR DEFAULT NULL )
  RETURN CRV_SEGMENTS PIPELINED
/*
*/
IS
BEGIN
  FOR ao_rec IN (
    with curves as (
      select object_id, field_id, scenario_id, curve
        from crv_sql_curves
       where object_id = id and field_id = p_field_id and scenario_id = p_scenario_id
    )
    select * from table( crv_sql_curve_pkg.segments_table( cursor( select * from curves ), p_start, p_finish, p_options ) )
  )
  LOOP
    pipe row( CRV_SEGMENT( ao_rec.id, ao_rec.field, ao_rec.scenario, ao_rec.segment_start, ao_rec.segment_finish, ao_rec.rate ) );
  END LOOP;
  RETURN;
END;
