CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_SEGMENTS_TABLE_FCT" ( p_cursor SYS_REFCURSOR, p_start INTEGER DEFAULT 2, p_finish INTEGER DEFAULT 65534, p_options VARCHAR DEFAULT NULL  )
RETURN CRV_SEGMENTS PIPELINED PARALLEL_ENABLE ( PARTITION p_cursor BY ANY )
IS
BEGIN
  FOR ao_rec IN (
    select * from table( crv_sql_curve_pkg.segments_table( p_cursor, p_start, p_finish, p_options ) )
  )
  LOOP
    pipe row( CRV_SEGMENT( ao_rec.id, ao_rec.field, ao_rec.scenario, ao_rec.segment_start, ao_rec.segment_finish, ao_rec.rate ) );
  END LOOP;
  RETURN;
END;
