Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."CRV_SEGMENT_SLICES_TABLE_FCT" ( p_cursor SYS_REFCURSOR )
RETURN CRV_SLICES PIPELINED PARALLEL_ENABLE ( PARTITION p_cursor BY ANY )
IS
BEGIN
  FOR ao_rec IN (
    select * from table( crv_sql_curve_pkg.segment_slices_table( p_cursor ) )
  )
  LOOP
    pipe row( CRV_SLICE( ao_rec.id, ao_rec.field, ao_rec.scenario, ao_rec.period, ao_rec.start_date, ao_rec.finish_date, ao_rec.slice, ao_rec.calendar_key, ao_rec.period_name, ao_rec.group_by ) );
  END LOOP;
  RETURN;
END;
