Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."DWH_CAL_DATEDIFF_FCT" (
    p_end_date IN DATE,     /* Date to subtract from.  */
    p_start_date IN DATE    /* Date to subtract.       */
) 
    RETURN INTEGER
IS
    days INTEGER;
BEGIN
    days := TRUNC(p_end_date) - TRUNC(p_start_date);
    RETURN days;
END DWH_CAL_DATEDIFF_FCT;
