Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."DWH_CAL_DATEFORMAT_FCT" (
    p_language_code VARCHAR,
    p_period_type VARCHAR
)
  RETURN VARCHAR
IS
    v_date_format VARCHAR(20);
BEGIN 
    IF p_period_type = 'D' OR p_period_type = 'W' THEN
        IF p_language_code = 'cs' THEN
            v_date_format := 'D. M. YYYY';
        ELSIF p_language_code = 'da' THEN
            v_date_format := 'DD-MM-YYYY';
        ELSIF p_language_code = 'de' THEN
            v_date_format := 'DD/MM/YYYY';
        ELSIF p_language_code = 'en' THEN
            v_date_format := 'MM/DD/YYYY';
        ELSIF p_language_code = 'es' THEN
            v_date_format := 'DD/MM/YYYY';
        ELSIF p_language_code = 'fi' THEN
            v_date_format := 'D.M.YYYY';
        ELSIF p_language_code = 'fr' THEN
            v_date_format := 'DD/MM/YYYY';
        ELSIF p_language_code = 'hu' THEN
            v_date_format := 'YYYY. MM. D';
        ELSIF p_language_code = 'it' THEN
            v_date_format := 'DD/MM/YYYY';
        ELSIF p_language_code = 'ja' THEN
            v_date_format := 'YYYY年MM月DD日';
        ELSIF p_language_code = 'ko' THEN
            v_date_format := 'YYYY.MM.DD';
        ELSIF p_language_code = 'nl' THEN
            v_date_format := 'DD-MM-YYYY';
        ELSIF p_language_code = 'no' THEN
            v_date_format := 'DD.MM.YYYY';
        ELSIF p_language_code = 'pl' THEN
            v_date_format := 'DD.MM.YYYY';
        ELSIF p_language_code = 'pt' THEN
            v_date_format := 'DD/MM/YYYY';
        ELSIF p_language_code = 'ru' THEN
            v_date_format := 'DD.MM.YYYY';
        ELSIF p_language_code = 'sv' THEN
            v_date_format := 'YYYY-MM-DD';
        ELSIF p_language_code = 'tr' THEN
            v_date_format := 'DD.MM.YYYY';
        ELSIF p_language_code = 'zh' THEN
            v_date_format := 'YYYY年M月D日';
        ELSIF p_language_code = 'zh_TW' THEN
            v_date_format := 'YYYY年M月D日';
        END IF;
    ELSIF p_period_type = 'M' THEN
        IF p_language_code = 'cs' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'da' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'de' THEN
            v_date_format := 'MM YY';
        ELSIF p_language_code = 'en' THEN
            v_date_format := 'MM-YY';
        ELSIF p_language_code = 'es' THEN
            v_date_format := 'MM-YY';
        ELSIF p_language_code = 'fi' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'fr' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'hu' THEN
            v_date_format := 'YY. MM';
        ELSIF p_language_code = 'it' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'ja' THEN
            v_date_format := 'YY 年 MM';
        ELSIF p_language_code = 'ko' THEN
            v_date_format := 'YY 년 MM';
        ELSIF p_language_code = 'nl' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'no' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'pl' THEN
            v_date_format := 'MM-YY';
        ELSIF p_language_code = 'pt' THEN
            v_date_format := 'MM/YY';
        ELSIF p_language_code = 'ru' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'sv' THEN
            v_date_format := 'MM-YY';
        ELSIF p_language_code = 'tr' THEN
            v_date_format := 'MM YYYY';
        ELSIF p_language_code = 'zh' THEN
            v_date_format := 'YYYY年MM';
        ELSIF p_language_code = 'zh_TW' THEN
            v_date_format := 'YYYY年MM';
        END IF;
    ELSIF p_period_type = 'Q' THEN
        IF p_language_code = 'cs' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'da' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'de' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'en' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'es' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'fi' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'fr' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'hu' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'it' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'ja' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'ko' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'nl' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'no' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'pl' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'pt' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'ru' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'sv' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'tr' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'zh' THEN
            v_date_format := 'QQ-YYYY';
        ELSIF p_language_code = 'zh_TW' THEN
            v_date_format := 'QQ-YYYY';
        END IF;
    ELSIF p_period_type = 'A' THEN
        IF p_language_code = 'cs' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'da' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'de' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'en' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'es' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'fi' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'fr' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'hu' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'it' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'ja' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'ko' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'nl' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'no' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'pl' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'pt' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'ru' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'sv' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'tr' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'zh' THEN
            v_date_format := 'YYYY';
        ELSIF p_language_code = 'zh_TW' THEN
            v_date_format := 'YYYY';
        END IF;
    END IF;
    RETURN v_date_format;
END;
