Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."IS_CUSTOMER_DB" 
RETURN NUMBER
IS
  v_is_customer_db INTEGER;
  v_base_version VARCHAR2(100);
  v_release_version VARCHAR2(100);
BEGIN

    SELECT count(*)
    INTO v_base_version
    FROM cmn_install_history
    WHERE install_type = 'release_marker'
    AND install_id = 'base_version';

    SELECT count(*) into v_release_version
    FROM cmn_install_history
    WHERE install_type = 'release_marker'
    AND install_id = 'release_version';

    IF( v_base_version = 0 or v_release_version > 0 )
    THEN
      v_is_customer_db := 1;
    ELSE
      v_is_customer_db := 0;
    END IF;

  RETURN v_is_customer_db;
END;
