Record.definition:
CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."ITL_GET_ACTUAL_EFFORT_FCT" ( P_INCIDENT_ID IN NUMBER, P_INV_ID IN NUMBER DEFAULT NULL ) RETURN NUMBER IS V_RETURN_VALUE NUMBER(32,6); BEGIN
IF P_INV_ID IS NOT NULL THEN SELECT NVL((TIMESHEET_DATA.EFFORT + IMPORTED_DATA.EFFORT),0) INTO V_RETURN_VALUE FROM (SELECT NVL(SUM(PRTIMEENTRY.PRACTSUM)/3600,0) EFFORT FROM PRTIMEENTRY, PRTIMESHEET, IMM_INCIDENTS WHERE PRTIMEENTRY.INCIDENT_ID = IMM_INCIDENTS.ID AND PRTIMEENTRY.PRTIMESHEETID = PRTIMESHEET.PRID AND PRTIMESHEET.PRSTATUS != 5 AND PRTIMEENTRY.INCIDENT_ID = P_INCIDENT_ID AND PRTIMEENTRY.INCIDENT_INVESTMENT_ID = P_INV_ID ) TIMESHEET_DATA, (SELECT NVL(SUM(QUANTITY),0) EFFORT FROM FIN_TXNS, IMM_INCIDENTS WHERE SOURCE_MODULE = 50 AND FIN_TXNS.PK_ID = IMM_INCIDENTS.ID AND FIN_TXNS.TABLE_NAME = 'IMM_INCIDENTS' AND FIN_TXNS.PK_ID = P_INCIDENT_ID) IMPORTED_DATA; ELSE SELECT NVL((TIMESHEET_DATA.EFFORT + IMPORTED_DATA.EFFORT),0) INTO V_RETURN_VALUE FROM (SELECT NVL(SUM(PRTIMEENTRY.PRACTSUM)/3600,0) EFFORT FROM PRTIMEENTRY, PRTIMESHEET, IMM_INCIDENTS WHERE PRTIMEENTRY.INCIDENT_ID = IMM_INCIDENTS.ID AND PRTIMEENTRY.PRTIMESHEETID = PRTIMESHEET.PRID AND PRTIMESHEET.PRSTATUS != 5 AND PRTIMEENTRY.INCIDENT_ID = P_INCIDENT_ID ) TIMESHEET_DATA, (SELECT NVL(SUM(QUANTITY),0) EFFORT FROM FIN_TXNS, IMM_INCIDENTS WHERE SOURCE_MODULE = 50 AND FIN_TXNS.PK_ID = IMM_INCIDENTS.ID AND FIN_TXNS.TABLE_NAME = 'IMM_INCIDENTS' AND FIN_TXNS.PK_ID = P_INCIDENT_ID) IMPORTED_DATA; END IF; RETURN V_RETURN_VALUE;
END;
