Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."NBI_GET_BILLING_PROJECT_FCT" (P_PROJECT_ID INTEGER)
   RETURN INTEGER
IS
   V_PROJECT_ID                  INTEGER := 0;
   V_BILLING_PROJECT_ID          INTEGER := 0;
BEGIN
   V_PROJECT_ID := P_PROJECT_ID;
   SELECT   NVL (MAX (BILLING_PROJECT_ID), -9)
   INTO     V_BILLING_PROJECT_ID
   FROM     PAC_MNT_PROJECTS
   WHERE    ID = P_PROJECT_ID;

   IF V_BILLING_PROJECT_ID not in ( -9, V_PROJECT_ID )
   THEN
      V_PROJECT_ID := NBI_GET_BILLING_PROJECT_FCT (V_BILLING_PROJECT_ID);
   END IF;

   RETURN V_PROJECT_ID;
EXCEPTION
   WHEN OTHERS
   THEN
      RAISE_APPLICATION_ERROR (-20000, 'Error in NBI_GET_BILLING_PROJECT_FCT - ' || SQLERRM);
END;
