Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."NBI_GET_OBS_PATH_FCT" (P_UNIT_ID INTEGER)
   RETURN VARCHAR2
IS
   V_PATH                        VARCHAR2 (1000);
BEGIN
   SELECT   NVL (MAX (PATH), NULL)
   INTO     V_PATH
   FROM     NBI_DIM_OBS
   WHERE    OBS_UNIT_ID = P_UNIT_ID;
   RETURN (V_PATH);
EXCEPTION
   WHEN OTHERS
   THEN
      RAISE_APPLICATION_ERROR (-20000, 'Error in NBI_GET_OBS_PATH_FCT - ' || SQLERRM);
END;
