Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."NBI_OBS_UNIT_ISLEAF_FCT" (P_UNIT_ID INTEGER)
   RETURN INTEGER
IS
   V_IS_LEAF                     INTEGER;
   V_COUNT                       INTEGER;
BEGIN
   SELECT   COUNT (*)
   INTO     V_COUNT
   FROM     PRJ_OBS_UNITS
   WHERE    PARENT_ID = P_UNIT_ID;

   IF V_COUNT = 0
   THEN
      V_IS_LEAF := 1;
   ELSE
      V_IS_LEAF := 0;
   END IF;

   RETURN (V_IS_LEAF);

EXCEPTION
   WHEN OTHERS
   THEN
      RAISE_APPLICATION_ERROR (-20000, 'Error in NBI_OBS_UNIT_ISLEAF_FCT - ' || SQLERRM);
END;
