Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."ODF_AUD_DATE_FCT" 
  (P_DATE_IN DATE)
  RETURN VARCHAR2 IS
  V_RETURN_VALUE VARCHAR2(19);
BEGIN
  IF P_DATE_IN IS NULL THEN
       V_RETURN_VALUE := NULL;
  ELSE
      V_RETURN_VALUE := TO_CHAR(P_DATE_IN, 'YYYY-MM-DD')||'T'||TO_CHAR(P_DATE_IN, 'HH24:MI:SS');
  END IF;
  RETURN V_RETURN_VALUE;
END;
