Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PAC_CHG_GL_ACCOUNT_CODES_FCT" 
 (P_ALLOCATION_DATES_ID NUMBER)
  RETURN VARCHAR2
IS
  V_ACCOUNT_CODES   VARCHAR2(1000) := ' ';
  V_NEXT            VARCHAR2(3) := ' ';

  CURSOR C_ACCT_CODES
      IS SELECT A.ACCOUNT_CODE || ' (' || TO_CHAR(D.ALLOCATION_PERCENT) || '%)' "ACCOUNT_CODE"
           FROM PAC_CHG_ALLOCS_DETAILS D,
                PAC_CHG_GL_ACCOUNTS A
          WHERE D.ALLOCATION_DATES_ID = P_ALLOCATION_DATES_ID
            AND D.ACCOUNT_CODE_ID = A.ID
       ORDER BY D.ID;
BEGIN
  FOR C_ACCT_CODES_REC IN C_ACCT_CODES
    LOOP
      IF LENGTH(V_ACCOUNT_CODES) < 255 THEN
        V_ACCOUNT_CODES := V_ACCOUNT_CODES || V_NEXT || C_ACCT_CODES_REC.ACCOUNT_CODE;
      END IF;

      IF V_NEXT = ' ' THEN
        V_NEXT := ', ';
      END IF;
    END LOOP;

  IF LENGTH(V_ACCOUNT_CODES) > 255 THEN
    V_ACCOUNT_CODES := SUBSTR(V_ACCOUNT_CODES, 1, 250) + '...';
  END IF;

  RETURN LTRIM(V_ACCOUNT_CODES);
END;
