Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PAC_CHG_PREVIEW_CR_ACCTS_FCT" 
(
  P_DATES_ID        NUMBER
)
  RETURN VARCHAR2
IS
  V_ACCOUNT_CODES   VARCHAR2(1000) := ' ';
  V_ACCOUNT_CODE    VARCHAR2(1000) := ' ';
  V_NEXT            VARCHAR2(3) := ' ';
  V_TO_DATE         DATE;

  CURSOR C_ACCT_CODES (C_DATES_ID NUMBER)
      IS SELECT DISTINCT D.ID
           FROM PAC_CHG_RESCREDIT_DETAILS D
          WHERE D.RES_CREDIT_DATES_ID = C_DATES_ID
       ORDER BY D.ID;

BEGIN
  V_TO_DATE := TO_DATE('12/31/2999', 'MM/DD/YYYY');

  FOR C_ACCT_CODES_REC IN C_ACCT_CODES(P_DATES_ID)
    LOOP
      IF LENGTH(V_ACCOUNT_CODES) < 255 THEN
        SELECT ACCOUNT_CODE || ' (' || TO_CHAR(ALLOCATION_PERCENT) || '%)'
          INTO V_ACCOUNT_CODE
          FROM PAC_CHG_RESCREDIT_DETAILS D,
                         PAC_CHG_GL_ACCOUNTS A
         WHERE D.ID = C_ACCT_CODES_REC.ID
           AND D.ACCOUNT_CODE_ID = A.ID;

        V_ACCOUNT_CODES := V_ACCOUNT_CODES || V_NEXT || V_ACCOUNT_CODE;
      END IF;

      IF V_NEXT = ' ' THEN
        V_NEXT := ', ';
      END IF;
    END LOOP;

  IF LENGTH(V_ACCOUNT_CODES) > 255 THEN
    V_ACCOUNT_CODES := SUBSTR(V_ACCOUNT_CODES, 1, 250) || '...';
  END IF;

  RETURN LTRIM(V_ACCOUNT_CODES);
END;
