Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_CALENDAR_FCT" ( p_start DATE, p_period_type VARCHAR, p_periods NUMBER, p_cal_type VARCHAR DEFAULT 'DEFAULT', p_entity NUMBER DEFAULT 0 )
   RETURN PFM_CALENDAR
IS
 v_calendar PFM_CALENDAR;

BEGIN
  CASE
    WHEN p_cal_type ='NBI' THEN
      v_calendar := PFM_NBI_CALENDAR_FCT( p_start, p_period_type, p_periods );
    WHEN p_cal_type ='FISCAL' THEN
      v_calendar := PFM_FISCAL_CALENDAR_FCT( p_start, p_period_type, p_periods, p_entity );
    WHEN p_cal_type ='RPT' THEN
      v_calendar := PFM_RPT_CALENDAR_FCT( p_start, p_period_type, p_periods );
    ELSE
      v_calendar := PFM_DEFAULT_CALENDAR_FCT( p_start, p_period_type, p_periods );
    END CASE ;
  return v_calendar;
END;
