Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_PORT_PH_SUM_FCT" (p_portfolio NUMBER, p_plan NUMBER, p_attr_name VARCHAR, p_object NUMBER DEFAULT 0 )
   RETURN NUMBER
IS
 v_return NUMBER;
 v_test INTEGER;
 v_start DATE;
 v_finish DATE;
 v_curve CLOB;
BEGIN

  IF( p_plan > 0 ) THEN
    SELECT START_DATE, FINISH_DATE INTO v_start,v_finish FROM PFM_PLANS WHERE ID = p_plan;
    v_test := 1;
  ELSE
    SELECT START_DATE, FINISH_DATE INTO v_start, v_finish FROM PFM_PORTFOLIOS WHERE ID = p_portfolio;
    v_test := 0;
  END IF;

  select NVL( pln.clob_curve, port.clob_curve ) INTO v_curve
    from pfm_portfolios p
    left outer join pfm_clob_curves port on p.id = port.id and port.plan_id = 0 and port.attribute = p_attr_name and port.object_id = p_object and port.table_name = 'PFM_PORTFOLIOS'
    left outer join pfm_clob_curves pln on p.id = pln.id and pln.plan_id = p_plan and pln.attribute = p_attr_name and pln.object_id = p_object and pln.table_name = 'PFM_PORTFOLIOS' and 1 = v_test
   where p.id = p_portfolio;

  IF( v_curve IS NOT NULL ) THEN
    v_return := PFM_SUM_FCT( v_curve, v_start, v_finish );
  ELSE
    v_return := 0;
  END IF;

  RETURN v_return;
END;
