Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_PORT_TOTAL_ROLE_PH_SUM_FCT" (p_portfolio NUMBER, p_plan NUMBER, p_attr_name VARCHAR )
   RETURN NUMBER
IS
 v_return NUMBER;
 v_start DATE;
 v_finish DATE;

BEGIN

  v_return := 0;

  IF( p_plan > 0 ) THEN
    SELECT START_DATE, FINISH_DATE INTO v_start, v_finish FROM PFM_PLANS WHERE ID = p_plan;
  ELSE
    SELECT START_DATE, FINISH_DATE INTO v_start, v_finish FROM PFM_PORTFOLIOS WHERE ID = p_portfolio;
  END IF;

  FOR ao_rec IN (
      select port.clob_curve from pfm_clob_curves port, PFM_ROLE_CAPACITY rc
        where port.object_id = rc.role_id and rc.portfolio_id = port.id and rc.plan_id = port.plan_id
        and port.attribute = 'ROLE_CAPACITY' and port.table_name = 'PFM_PORTFOLIOS' and port.id = p_portfolio
        and port.plan_id = p_plan
  )
  LOOP
    -- get the slices
    IF ( ao_rec.clob_curve IS NOT NULL ) THEN
      v_return := v_return + PFM_SUM_FCT( ao_rec.clob_curve, v_start, v_finish );
    END IF;
  END LOOP;

  RETURN v_return;
END;
