Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_QUERY_FCT" ( p_segments PFM_SEGMENTS , p_start INTEGER DEFAULT 0, p_finish INTEGER DEFAULT 99999999  )
   RETURN NUMBER
/*
*/
IS
 v_return    NUMBER;

BEGIN
  v_return := 0;
  SELECT
    SUM( CASE
         WHEN segment_start >= p_start and segment_start <= p_finish and segment_finish >= p_finish THEN
            ( p_finish - segment_start ) * 86400 * rate
          WHEN segment_start <= p_start and segment_finish >= p_finish THEN
            ( p_finish - p_start ) * 86400 * rate
          WHEN segment_start <= p_start and segment_finish >= p_start and segment_finish <= p_finish THEN
            ( segment_finish - p_start ) * 86400 * rate
         WHEN segment_start >= p_start and segment_start <= p_finish and segment_finish >= p_start and segment_finish <= p_finish THEN
           ( segment_finish - segment_start ) * 86400 * rate
          END ) INTO v_return
          from table( p_segments );
  IF v_return is null THEN
    v_return:= 0;
  END IF;

  RETURN round( v_return, 2 );
END;
