Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_RPT_CALENDAR_FCT" ( p_start DATE, p_period_type VARCHAR, p_periods NUMBER )
   RETURN PFM_CALENDAR
IS
 v_calendar PFM_CALENDAR;
 v_start DATE;
 v_period INTEGER;
 v_period_type VARCHAR2(32);
BEGIN
  v_period := 0;
  v_start := trunc( p_start );
  CASE
    WHEN p_period_type ='WEEK' THEN v_period_type := 'WEEKLY';
    WHEN p_period_type ='MONTH' THEN v_period_type := 'MONTHLY';
    WHEN p_period_type ='QUARTER' THEN v_period_type := 'QUARTERLY';
    WHEN p_period_type ='YEAR' THEN v_period_type := 'YEARLY';
    ELSE v_period_type := p_period_type;
  END CASE;
  v_calendar := PFM_CALENDAR();
  FOR ao_rec IN (
    select start_date, end_date, fte from (
        select start_date, end_date, fte
          from rpt_calendar where period_type = v_period_type
           and start_date <= v_start and end_date >= v_start
        union
        select start_date, end_date, fte
          from rpt_calendar where period_type = v_period_type
           and end_date >= v_start
    )
    where rownum <= p_periods order by start_date  )
  LOOP
    v_period := v_period + 1;
    v_calendar.extend(1);
        v_calendar(v_period) := PFM_PERIOD( v_period, ao_rec.start_date, ao_rec.end_date+1, 'foo', ao_rec.fte );
  END LOOP;
  return v_calendar;
END;
