Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_SEGMENTS_FCT" ( ID NUMBER, p_curve CLOB, p_start INTEGER DEFAULT 0, p_finish INTEGER DEFAULT 99999999 )
   RETURN PFM_SEGMENTS
/*
*/

IS
 V_SEGMENTS PFM_SEGMENTS;
 IDX NUMBER;
 V_IDX NUMBER;
 V_S_IDX NUMBER;
 V_R_IDX NUMBER;
 V_START NUMBER;
 V_S_START NUMBER;
 V_SEGMENT_START INTEGER;
 V_SEGMENT_FINISH INTEGER;
 V_RATE FLOAT;
 V_CURVE VARCHAR2(32000);
 V_SEGMENT VARCHAR2(32000);
 V_DATES VARCHAR2(12);
BEGIN
  V_SEGMENTS := PFM_SEGMENTS();

  -- convert the clob to a varchar2(32000) if possible
  IF( LENGTH(P_CURVE) < 32000 ) THEN
    V_CURVE := P_CURVE;
  END IF;

  IDX := 0;
  V_IDX := 1;
  V_R_IDX :=1;
  V_S_IDX :=1;
  V_START := 1;
  V_S_START :=1;

  IF( V_CURVE IS NOT NULL ) THEN 
    V_IDX := INSTR( V_CURVE , '|', 1,1);
  ELSE
    V_IDX := INSTR( P_CURVE , '|', 1,1);
  END IF;
  WHILE V_IDX > 0
    LOOP
      V_R_IDX :=1;
      V_S_IDX :=1;
      V_S_START :=1;
      IF( V_CURVE IS NOT NULL ) THEN 
        V_SEGMENT := SUBSTR( V_CURVE, V_START, V_IDX - V_START );
      ELSE
        V_SEGMENT := SUBSTR( P_CURVE, V_START, V_IDX - V_START );
      END IF;
      V_R_IDX:=INSTR( V_SEGMENT, '@', 1,1);
      V_RATE := TO_NUMBER(SUBSTR(V_SEGMENT,1,V_R_IDX-V_S_START) );
      V_SEGMENT:=SUBSTR(V_SEGMENT,V_R_IDX+1);
      V_S_IDX:=INSTR( V_SEGMENT, ':', 1,1);
      WHILE V_S_IDX > 0
        LOOP
          V_DATES := SUBSTR(V_SEGMENT,V_S_START,V_S_IDX-V_S_START);
          V_SEGMENT_START := TO_NUMBER( SUBSTR( V_DATES, 1, 5) );
          V_SEGMENT_FINISH := V_SEGMENT_START+TO_NUMBER(SUBSTR( V_DATES, 7) );
          IF ( V_SEGMENT_FINISH > P_START AND V_SEGMENT_START <= P_FINISH ) THEN
            IDX := IDX + 1;
            V_SEGMENTS.EXTEND(1);
            V_SEGMENTS(IDX) := PFM_SEGMENT( ID, V_SEGMENT_START,V_SEGMENT_FINISH, V_RATE );
          END IF;
          IF ( V_SEGMENT_START > P_FINISH ) THEN
            V_S_IDX := -9999;
            V_IDX :=-9999;
          ELSE
            V_S_IDX := V_S_IDX + 1;
            V_S_START := V_S_IDX;
            V_S_IDX := INSTR( V_SEGMENT, ':', V_S_IDX,1);
          END IF;
        END LOOP;
      -- DO NOT REPOSITION IF IDX < 0 SINCE PARSING IS DONE
      IF( V_IDX > 0 ) THEN
        V_IDX := V_IDX + 1;
        V_START := V_IDX;
        IF( V_CURVE IS NOT NULL ) THEN 
          V_IDX := INSTR( V_CURVE, '|', V_IDX,1);
        ELSE
          V_IDX := INSTR( P_CURVE, '|', V_IDX,1);
        END IF;
      END IF;
    END LOOP;
  RETURN V_SEGMENTS;
END;
