Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PFM_SLICES_TABLE_FCT" (id NUMBER, p_curve clob , p_period_type VARCHAR, p_periods NUMBER, p_start DATE, p_cal_type VARCHAR DEFAULT 'DEFAULT', p_entity NUMBER DEFAULT 0 )
   RETURN PFM_SLICES PIPELINED

IS
 v_slices PFM_SLICES;
 v_calendar PFM_CALENDAR;
BEGIN
  -- get the calendar
  v_calendar := PFM_CALENDAR_FCT( p_start, p_period_type, p_periods, p_cal_type, p_entity );
  -- get the slices
  v_slices := PFM_SLICES_FCT( id, p_curve, v_calendar );
  -- return the results
  FOR i IN v_slices.FIRST..v_slices.LAST LOOP
    pipe row( v_slices(i) );
  END LOOP;
  return;
END;
