Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PRJ_DEFAULT_ALLOC_RATE_FCT" ( P_CURVE IN BLOB )
RETURN NUMBER
AS
  default_rate number;
  idx number; b_idx number := 1;
  V_CURVE BLOB := P_CURVE;
curve varchar2(32000);

BEGIN

  IF( P_CURVE IS NULL ) THEN
    RETURN 0.0;
  END IF;

  b_idx := length(v_curve) - 10;

  -- default rate?
  CRV_SQL_CURVE_PKG.READ_DOUBLE(v_curve, b_idx, curve, idx, default_rate );

  RETURN default_rate;
  -- safety check ... if anything goes wrong just return an empty curve
  EXCEPTION
    WHEN OTHERS THEN
      RETURN 0.0;
END;
