CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."PRJ_MAX_ACTTHRU_FCT" ( P_PROJECT_ID NUMBER, P_RESOURCE_ID NUMBER )
RETURN DATE /* Description: ------------ This function finds the maximum actthru date for the resource on the project */ IS V_ACTTHRU DATE; BEGIN SELECT MAX(PRACTTHRU) INTO V_ACTTHRU FROM PRASSIGNMENT , PRTEAM WHERE PRTEAM.PRRESOURCEID=P_RESOURCE_ID AND PRTEAM.PRPROJECTID=P_PROJECT_ID AND PRASSIGNMENT.TEAM_ID=PRTEAM.PRID;
RETURN (V_ACTTHRU);
END;
