Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."RPT_DATEADD_FCT" (
	P_INTERVAL_VALUE IN VARCHAR2,
	P_ADD_NUM IN NUMBER,
	P_DATE IN DATE
)
RETURN DATE
AS
  V_RESULT DATE;
BEGIN
  --
  IF LOWER(P_INTERVAL_VALUE) = 'd' OR LOWER(P_INTERVAL_VALUE) = 'dd' THEN
  V_RESULT := P_DATE + NUMTODSINTERVAL(P_ADD_NUM,'DAY');
  ELSIF LOWER(P_INTERVAL_VALUE) = 'w' OR LOWER(P_INTERVAL_VALUE) = 'ww' THEN
  V_RESULT := P_DATE + (7*P_ADD_NUM);
  ELSIF LOWER(P_INTERVAL_VALUE) = 'm' OR LOWER(P_INTERVAL_VALUE) = 'mm' THEN
  V_RESULT := add_months(P_DATE,P_ADD_NUM);
  ELSIF LOWER(P_INTERVAL_VALUE) = 'y' OR LOWER(P_INTERVAL_VALUE) = 'yy' OR LOWER(P_INTERVAL_VALUE) = 'yyyy' THEN
  V_RESULT := P_DATE + NUMTOYMINTERVAL(P_ADD_NUM,'YEAR');
  END IF;
  RETURN(V_RESULT);
END;
