Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."RPT_DATEPART_FCT" 
  (P_INTERVAL_VALUE VARCHAR2,P_DATE_FROM DATE)
  RETURN NUMBER AS

  V_RESULT NUMBER;
BEGIN



  IF UPPER(P_INTERVAL_VALUE) = 'DAY' THEN	 
  V_RESULT := TO_CHAR((P_DATE_FROM),'dd'); 
  END IF;
  IF UPPER(P_INTERVAL_VALUE) = 'WEEK' THEN	 
  V_RESULT := TO_CHAR((P_DATE_FROM),'D'); 
  END IF;
  IF UPPER(P_INTERVAL_VALUE) = 'MONTH' THEN	 
  V_RESULT := TO_CHAR((P_DATE_FROM),'mm'); 
  END IF;
  IF UPPER(P_INTERVAL_VALUE) = 'YEAR' THEN	 
  V_RESULT := TO_CHAR((P_DATE_FROM),'yyyy'); 
  END IF;



  RETURN V_RESULT;
END;
