Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."RPT_YES_NO_FCT" (
        p_value NUMBER,
        p_language VARCHAR2
)
RETURN VARCHAR2
IS
    v_value VARCHAR2(100);
BEGIN
    SELECT l.name
    INTO   v_value
    FROM   cmn_lookups_v l
    WHERE  l.lookup_type = 'RPT_YES_NO'
    AND    l.lookup_enum = p_value
    AND    l.language_code = p_language;
    RETURN v_value;
END;
