Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."XID_DOM_FIRST_FCT" (p_date DATE)
RETURN DATE IS
 v_Month VARCHAR2(2);
 v_Year VARCHAR2(4); 
BEGIN
  v_Month := TO_CHAR(p_date, 'MM');
  v_Year := TO_CHAR(p_date, 'YYYY');
  RETURN TO_DATE(v_Year || v_Month || '01', 'YYYYMMDD'); 
END XID_DOM_FIRST_FCT;
