Record.definition:
  CREATE OR REPLACE EDITIONABLE FUNCTION "PPM"."XID_DOM_LAST_FCT" (p_date DATE)
RETURN DATE IS
 v_date DATE;
 v_Month VARCHAR2(2);
 v_Year VARCHAR2(4); 
BEGIN
  v_date := add_months(p_date,1);
  v_Month := TO_CHAR(v_date, 'MM');
  v_Year := TO_CHAR(v_date, 'YYYY');
  RETURN TO_DATE(v_Year || v_Month || '01', 'YYYYMMDD')-1; 
END XID_DOM_LAST_FCT;
