Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."BB_ASSIGNINVOICENUMBER" (
  cClient   VARCHAR2 ,
  cProject  VARCHAR2 ,
  cGroupNo  VARCHAR2 ,
  cUserID   VARCHAR2 ,
  in_cInvoiceDate   IN VARCHAR2 ,
  cNextInvNo  IN OUT VARCHAR2,
  cBillTo   IN OUT VARCHAR2
)
AS
  cInvoiceDate  GLOBALPKG.STRING;
  StoO_selcnt INTEGER;
  StoO_error  INTEGER;
  StoO_rowcnt INTEGER;
  StoO_crowcnt  INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg GLOBALPKG.STRING;
  StoO_sqlstatus  INTEGER;
  msg   GLOBALPKG.STRING;
  dInvoiceDate  DATE;
BEGIN
 /* Called From:
     Procedure         :- BB_GENINVOICENUMBERS, INT_ASSIGNINVOICENUMBER
     Trigger           :- None
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   : Devdatta
   Modified Date : 07/25/2001
*/
	Bb_Assigninvoicenumber.cInvoiceDate := Bb_Assigninvoicenumber.in_cInvoiceDate;
	Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
	IF  Bb_Assigninvoicenumber.cInvoiceDate IS NULL THEN
		BEGIN
			StoO_rowcnt := 0;
			StoO_selcnt := 0;
			StoO_error  := 0;

      SELECT   MIN(RPAD(TO_CHAR(B.BillingDate,'mm/dd/yyyy'), 12, ' '))
			INTO Bb_Assigninvoicenumber.cInvoiceDate FROM PPA_BILLINGS B
			WHERE B.InvoiceNo = Bb_Assigninvoicenumber.cGroupNo
			 AND B.TRANSTYPE = 'B';
			StoO_rowcnt := SQL%ROWCOUNT;
			EXCEPTION
				WHEN TOO_MANY_ROWS THEN
					StoO_rowcnt := 2;
				WHEN NO_DATA_FOUND THEN
					StoO_rowcnt := 0;
					StoO_selcnt := 0;
				WHEN OTHERS THEN
					StoO_rowcnt := 0;
					StoO_selcnt := 0;
					StoO_error := SQLCODE;
					StoO_errmsg := SQLERRM;
		END;
	END IF;
	SAVEPOINT TRAN1;
	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE SEMAPHORE2
    SET value = 0;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	Bb_Assigninvoicenumber.dInvoiceDate :=  TO_DATE(Bb_Assigninvoicenumber.cInvoiceDate, 'mm/dd/yyyy');
	BEGIN
    Sp_Updateinvcreditmemono(TRANSTYPE=>'B',
    COMPANY_CODE=>Bb_Assigninvoicenumber.cClient,
    invcdate=>Bb_Assigninvoicenumber.dInvoiceDate,
    arinvcnmbr=>Bb_Assigninvoicenumber.cNextInvNo);
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error = 0 THEN
		COMMIT WORK;
	ELSE
		ROLLBACK TO SAVEPOINT TRAN1;
	END IF;
	BEGIN
		StoO_rowcnt := 0;
		StoO_selcnt := 0;
		StoO_error  := 0;

    SELECT DISTINCT  BILL_TO_COMPANY_CODE
		INTO Bb_Assigninvoicenumber.cBillTo
    FROM pac_mnt_projects
		WHERE COMPANY_CODE = Bb_Assigninvoicenumber.cClient
		 AND PROJECT_CODE = Bb_Assigninvoicenumber.cProject;
		StoO_rowcnt := SQL%ROWCOUNT;
		EXCEPTION
			WHEN TOO_MANY_ROWS THEN
				StoO_rowcnt := 2;
			WHEN NO_DATA_FOUND THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
			WHEN OTHERS THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
				StoO_error := SQLCODE;
				StoO_errmsg := SQLERRM;
	END;
	SAVEPOINT TRAN2;
	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE PPA_BILLINGS
    SET InvoiceNo = Bb_Assigninvoicenumber.cNextInvNo,
    BillingDate = Bb_Assigninvoicenumber.dInvoiceDate, --TO_DATE(Bb_Assigninvoicenumber.cInvoiceDate, 'mm/dd/yyyy'),
    LASTUPDTDBY_RESOURCE_CODE = Bb_Assigninvoicenumber.cUserID,
    LastUpdateDate = TRUNC(SYSDATE)
    WHERE InvoiceNo = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	END IF;
	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE INVOICEHEADER
    SET invoiceno = Bb_Assigninvoicenumber.cNextInvNo,
    invoiceno_applyto = Bb_Assigninvoicenumber.cNextInvNo,
    printed = 1,
    status = '0',
    BILL_TO_COMPANY_CODE = Bb_Assigninvoicenumber.cBillTo,
    invoicedate = Bb_Assigninvoicenumber.dInvoiceDate
    WHERE invoiceno = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	END IF;

	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;

    -- updating invoice number of values table
    UPDATE INVOICEHEADER_VALUES
    SET invoiceno = Bb_Assigninvoicenumber.cNextInvNo
    WHERE invoiceno = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	END IF;

	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;

    -- updating invoice number of PAC_BIL_INVOICE_TRN_DETAILS table
    UPDATE PAC_BIL_INVOICE_TRN_DETAILS
    SET Invoice_No = Bb_Assigninvoicenumber.cNextInvNo
    WHERE Invoice_No = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	END IF;

	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE PPA_INVOICEDETAILS
    SET InvoiceNo = Bb_Assigninvoicenumber.cNextInvNo
    WHERE InvoiceNo = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	END IF;

	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    -- update the invoice number of the values table also
    UPDATE PPA_INVOICEDETAILS_VALUES
    SET InvoiceNo = Bb_Assigninvoicenumber.cNextInvNo
    WHERE InvoiceNo = Bb_Assigninvoicenumber.cGroupNo;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
	IF  StoO_error != 0 THEN
	BEGIN
		ROLLBACK TO SAVEPOINT TRAN2;
		Bb_Assigninvoicenumber.cNextInvNo :=  NULL;
		RETURN ;
	END;
	ELSE
		COMMIT WORK;
	END IF;

END Bb_Assigninvoicenumber;
